content.OnBindClick('49.popup', function(plugins, event, target, item){
    
    let html = content.Render('49.popup');

    html = `
        <block for="html.modal" style="border-radius:5px; background: #171717;" > 
            ${html}
        </block>
    `;



    
    mdModal.ItemAdd({
        id: 'plugins_fontawesome-popup',
        overlay: {closeable: true, opacity:0},
        align: {x: 'left-out', 'y': 'top-in'},
        offset: {x: -60, y: 0},
        element: $(target),
        html: function()
        {
            return `<div style="width:100%; max-width:360px"> ${html} </div>`;
        }
    });
    
});

bind-input-search.js.code:
content.OnBindInput('49.search', function(plugins, event, target, item){
    let val = $(target).val().toLowerCase();

    $(target).closest(".fa-popup-wrapper").find(".icon-item").each(function(){
        let icon_cls = $(this).attr("data-class");
        if (icon_cls.includes(val)) {
            $(this).show();
        } else {
            $(this).hide();
        }
    });
});

