content.RenderCreate('49.popup', function(content, data, tag){
    let weights = {
        'fa-brands': 'Brands',
        'fa-solid': 'Regular'
    };


    let ht = `  
        <div class="fa-popup-wrapper">

            <div class="filters">

                ${
                    html.Render('select', {
                        'value': 'fa-solid',
                        options: weights,
                        'placeholder': 'Select type',
                        attributes: {
                            'onchange':"content.Render('49.icons', {weight:this.value}, {}, true, true)",
                        },
                    }, {
                        class: 'gray',
                    })
                }
           
                ${
                    builder.Render('element.input', 
                        {
                            'attr': 'bind-input="49.search|builder.content"',
                            'placeholder': 'Search...'
                        }
                    )
                }
            </div>

            ${content.Render('49.icons', { weight:'fa-solid'})}

        </div>
        `;

    return ht;
});

bind-click-set.js.code:
content.OnBindClick('49.set', function(plugins, event, target, item){


    /* Get two classes of clicked icon */
    let cls = $(target).attr("data-class");
    let weight = $(target).attr("data-weight");

    /* get preivous classes */
    let tag = edit.Fn('get.tag');
    let current_cls = tag.Fn('attributes.get.object', 'class').value;
    carr = [];
    if (current_cls) {
        carr = current_cls.split(" ");
    }
    
    /* add new classes to new array */
    let newclass_array = [cls, weight];

    $.each(carr, function(ind, clasname ){
        /* add previous classes (not including fa- to new array) */
        if (clasname.substring(0, 3) !== "fa-") {
            newclass_array.push(clasname);
        }
    });

    let final_class = newclass_array.join(" ");

    tag.Fn('attributes.set.key', 'class', final_class ? final_class : null);
    tag.Fn('update.attributes');


    content.Render('49.settings', {}, {}, true, true);
});

render-settings.js.code:
content.RenderCreate( '49.settings', function(plugins, data){

    let current_icon = '';
    let tag = edit.Fn('get.tag');
    let current_cls = tag.Fn('attributes.get.object', 'class').value;
    
    carr = [];
    if (current_cls) {
        carr = current_cls.split(" ");
    }
    
    $.each(carr, function(ind, clasname ){
        if (clasname.substring(0, 3) === "fa-") {
            current_icon+=clasname + " ";
        }
    });

    if (!current_icon) {current_icon = "fa-regular fa-image";}


    let html = `  
        <div>
            <i class="${current_icon}"></i>
        </div>
        <span bind-click="49.popup|builder.content"> Change icon </span> 
    `;

    return html;
});

